package org.example

class Coche(private var _marca: String = "Ford", private var _modelo: Int = 2020) {
    var marca: String
        get() = _marca
        set(value) {
            _marca = value
        }
    var modelo: Int
        get() = _modelo
        set(value) {
            _modelo = value
        }
}

interface Perimetrable {
    fun calcularPerimetro(): Double}

class Circulo(private val radio: Double) : Perimetrable {
    override fun calcularPerimetro(): Double {
        return 2 * Math.PI * radio
    }
}

class Rectangulo(private val largo: Double, private val ancho: Double) : Perimetrable {
    override fun calcularPerimetro(): Double {
        return 2 * (largo + ancho)
    }
}

fun mostrarPerimetros() {
    val figuras: List<Perimetrable> = listOf(
        Circulo(5.0),
        Rectangulo(4.0, 6.0)
    )

    for (figura in figuras) {
        println("Per√≠metro: ${figura.calcularPerimetro()}")
    }
}

fun main() {

    val coche = Coche("Chevrolet", 2025)

    println("Marca ${coche.marca}")
    println("Modelo ${coche.modelo}")

    mostrarPerimetros()
}

